```dart
mixin ContainerRenderObjectMixin on RenderObjectElement {
  List<Element> _children;
  final Set<Element> _forgottenChildren = HashSet<Element>();

  ...

  // Rather than overriding forgetChild, I give it a new name.
  void forgetListChild(Element child) {
    assert(_children.contains(child));
    assert(!_forgottenChildren.contains(child));
    _forgottenChildren.add(child);
    super.forgetChild(child);
  }

  ...

}

// Then I use the mixin here in my code.
class _CupertinoTextSelectionToolbarItemsElement extends RenderObjectElement with ContainerRenderObjectMixin {

  ...

  @override
  void forgetChild(Element child) {
    // Handle children in slots like normal.
    if (childToSlot.containsKey(child)) {
      final _CupertinoTextSelectionToolbarItemsSlot slot = childToSlot[child];
      childToSlot.remove(child);
      slotToChild.remove(slot);
      // Super calls to RenderObjectElement.forgetChild as desired.
      super.forgetChild(child);

    // Handle list children with the mixin, which can also call to RenderObjectElement.forgetChild.
    } else {
      forgetListChild(child);
    }
  }

  ...

}

// And we can also use the mixin in MultiChildRenderObjectElement to deduplicate code.
class MultiChildRenderObjectElement extends RenderObjectElement with ContainerRenderObjectMixin {

  ...

  @override
  void forgetChild(Element child) {
    forgetListChild(child);
  }

  ...

}
