```suggestion
export interface OnfidoDocumentResponse {
  id: string;
  side: string;
  type: string;
}

export interface OnfidoFaceResponse {
  id: string;
  variant: string;
}

export interface OnfidoResponse {
  document_front: OnfidoDocumentResponse;
  document_back?: OnfidoDocumentResponse;
  face: OnfidoFaceResponse;
}

export interface OnfidoError {
  type: 'exception' | 'expired_token';
  message: string;
}

export type OnfidoSupportedLanguages =
  | 'en_US'
  | 'en'
  | 'de_DE'
  | 'de'
  | 'es_ES'
  | 'es'
  | 'fr_FR'
  | 'fr';

export interface OnfidoLocaleConfig {
  language: OnfidoSupportedLanguages;
  phrases: Record<string, unknown>;
  mobilePhrases?: Record<string, unknown>;
}

export type OnfidoStepTypes =
  | 'welcome'
  | 'document'
  | 'poa'
  | 'face'
  | 'complete';

export interface OnfidoStepOptionWelcome {
  title?: string;
  descriptions?: string[];
  nextButton?: string;
}

export interface OnfidoStepOptionDocument {
  documentTypes?: {
    passport?: boolean;
    driving_licence?: boolean;
    national_identity_card?: boolean;
    residence_permit?: boolean;
  };
  showCountrySelection?: boolean;
  forceCrossDevice?: boolean;
  useLiveDocumentCapture?: boolean;
  uploadFallback?: boolean;
}

export interface OnfidoStepOptionPoA {
  country?: string;
  documentTypes: {
    bank_building_society_statement?: boolean;
    utility_bill?: boolean;
    council_tax?: boolean; // GBR only
    benefit_letters?: boolean; // GBR only
    government_letter?: boolean; // non-GBR only
  };
}

export interface OnfidoStepOptionFace {
  requestedVariant?: 'standard' | 'video';
  uploadFallback?: boolean;
  useMultipleSelfieCapture?: boolean;
}

export interface OnfidoStepOptionComplete {
  message?: string;
  submessage?: string;
}

export interface OnfidoStepConfig {
  type: OnfidoStepTypes;
  options:
    | OnfidoStepOptionWelcome
    | OnfidoStepOptionDocument
    | OnfidoStepOptionPoA
    | OnfidoStepOptionFace
    | OnfidoStepOptionComplete;
}

export interface OnfidoConfig {
  // Callbacks
  onComplete?: (data: OnfidoResponse) => void;
  onError?: (error: OnfidoError) => void;
  onModalRequestClose?: () => void;

  // Customization
  token: string;
  useModal?: boolean;
  isModalOpen?: boolean;
  shouldCloseOnOverlayClick?: boolean;
  containerId?: string;
  containerEl?: HTMLElement | null;
  language?: OnfidoSupportedLanguages | OnfidoLocaleConfig;
  smsNumberCountryCode?: string;
  userDetails?: {
    smsNumber?: string;
  };
  steps?: Array<OnfidoStepTypes | OnfidoStepConfig>;
}

export interface OnfidoHandle {
  setOptions(opts: OnfidoConfig): void;
  tearDown(): void;
}

export function init(options: OnfidoConfig): OnfidoHandle;
```
